mapper.select:
PREPARE s1 FROM '
    select aa.date, count(t_web_visit.id) as num from
    (SELECT date_format( adddate( ${startTime}, @num := @num + 1 ), \'%Y-%m-%d\' ) date
    FROM t_web_visit t_web_visit, ( SELECT @num :=- 1 ) num LIMIT ?) aa
    left join t_web_visit t_web_visit
    on DATE_FORMAT(t_web_visit.operate_time, \'%Y-%m-%d\') = aa.date
    and t_web_visit.is_delete = 0 and status = \'1\'
    group by aa.date order by aa.date';
set @_limit= DATEDIFF( ${endTime}, ${startTime} ) + 1;
EXECUTE s1 USING @_limit;

PREPARE s1 FROM '
    select aa.date, count(distinct t_web_visit.admin_id) as num from
    (SELECT date_format( adddate( ${startTime}, @num := @num + 1 ), \'%Y-%m-%d\' ) date
    FROM t_web_visit t_web_visit, ( SELECT @num :=- 1 ) num LIMIT ?) aa
    left join t_web_visit t_web_visit
    on DATE_FORMAT(t_web_visit.operate_time, \'%Y-%m-%d\') = aa.date
    and t_web_visit.is_delete = 0 and status = \'1\'
    group by aa.date order by aa.date';
set @_limit= DATEDIFF( ${endTime}, ${startTime} ) + 1;
EXECUTE s1 USING @_limit;
